import React, { useState } from "react";
import { Container, CssBaseline, Grid, Typography } from "@material-ui/core";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Box from "@material-ui/core/Box";

import courseDetailsStyles from "./CourseDetailsStyle";
import Header from "../../Header/Header";
import Footer from "../../Footer/Footer";

// ////////////////////////////////////////
// for tabs //////////////////////////////////
// function TabPanel(props) {
//   const { children, value, index, ...other } = props;

//   return (
//     <div
//       role="tabpanel"
//       hidden={value !== index}
//       id={`simple-tabpanel-${index}`}
//       aria-labelledby={`simple-tab-${index}`}
//       {...other}
//     >
//       {value === index && (
//         <Box p={3}>
//           <Typography>{children}</Typography>
//         </Box>
//       )}
//     </div>
//   );
// }

// TabPanel.propTypes = {
//   children: PropTypes.node,
//   index: PropTypes.any.isRequired,
//   value: PropTypes.any.isRequired,
// };

// const useStyles = makeStyles((theme) => ({
//   root: {
//     flexGrow: 1,
//     backgroundColor: theme.palette.background.paper,
//   },
// }));
// //////////////////////////////////////////////////////////////////

const CourseDetails = () => {
  const classes = courseDetailsStyles();
  const [selectedTab, setSelectedTab] = useState(0);

  const handleChange = (event, newValue) => {
    setSelectedTab(newValue);
  };
  return (
    <>
      <CssBaseline />
      {/* header component start */}
      <Header></Header>
      {/* header component end */}
      <Container>
        {/* container to initially contain video player and details */}
        <Grid className={classes.courseDetailsContainer} container spacing={2}>
          {/* video player */}
          <Grid
            container
            className={classes.videoPlayerContainer}
            justify="flex-start"
            xs={12}
            lg={9}
          >
            {/* course title, will be dynamic */}
            <Typography variant="h4">
              PC : 101 - Introduction to Education
            </Typography>
            {/* video player here.. */}
            <img
              className={classes.courseImgContainer}
              src="https://images.unsplash.com/photo-1509062522246-3755977927d7?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=804&q=80"
              alt="course_img"
            />

            {/* tabs section */}

            <div className={classes.tabsContainer}>
              {/* <AppBar position="static"> */}
              <Tabs value={selectedTab} onChange={handleChange}>
                <Tab label="Description" />
                <Tab label="Curriculum" />
                <Tab label="Notes & PDF" />
                <Tab label="FAQ" />
              </Tabs>
              {/* </AppBar> */}
              {/* <TabPanel value={value} index={0}>
              Item One
            </TabPanel>
            <TabPanel value={value} index={1}>
              Item Two
            </TabPanel>
            <TabPanel value={value} index={2}>
              Item Three
            </TabPanel> */}
            </div>
          </Grid>

          {/* for details */}
          <Grid
            container
            className={classes.detailsContainer}
            justify="center"
            xs={12}
            lg={3}
          >
            Course details
          </Grid>
        </Grid>
      </Container>

      {/* footer component start */}
      {/* <Footer></Footer> */}
      {/* footer component end */}
    </>
  );
};

export default CourseDetails;
