import {
  Box,
  Button,
  Container,
  CssBaseline,
  Typography,
} from "@material-ui/core";
import React from "react";
import coursesStyles from "./CoursesStyle";
import Grid from "@material-ui/core/Grid";
import Header from "../Header/Header";

const Courses = () => {
  const classes = coursesStyles();
  return (
    <>
      <CssBaseline />

      {/* header component */}
      <Header />
      {/* header component */}
      <Box borderTop={3} className={classes.borderContainer}></Box>
      <Container>
        {/* initial grid container for category & description  */}
        <Grid container justify="center" alignItems="center" spacing={2}>
          {/* for categories */}
          <Grid
            container
            justify="center"
            alignItems="center"
            item
            xs={12}
            lg={4}
          ></Grid>

          {/* for courses list */}
          <Grid
            container
            justify="center"
            alignItems="center"
            item
            xs={12}
            lg={8}
          ></Grid>
        </Grid>
      </Container>
    </>
  );
};

export default Courses;
