import { makeStyles } from "@material-ui/core/styles";

const footerStyles = makeStyles((theme) => ({
  footerContainer: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(2),
  },
  footerImg: {
    width: "70%",
    height: "90%",
    backgroundColor: "#212121",
  },
}));

export default footerStyles;
