import React from "react";
import {
  Avatar,
  Card,
  CardHeader,
  CardMedia,
  Container,
  Grid,
  Typography,
} from "@material-ui/core";
import courseDetailsStyles from "./CourseDetailsStyle";
import Header from "../../Header/Header";

const CourseDetails = () => {
  const classes = courseDetailsStyles();
  return (
    <>
      {/* header component start */}
      <Header></Header>
      {/* header component end */}
      <Container>
        {/* container to initially contain video player and details */}
        <Grid className={classes.courseDetailsContainer} container spacing={2}>
          {/* video player */}
          <Grid
            container
            className={classes.videoPlayerContainer}
            justify="flex-start"
            xs={12}
            lg={9}
          >
            {/* course title, subheader will be dynamic */}
            <Card>
              <CardHeader
                avatar={
                  <Avatar aria-label="courseName" className={classes.avatar}>
                    PC
                  </Avatar>
                }
                title="PC - 101 : Introduction to Education"
                subheader="September 14, 2016"
              />
              {/* <Typography variant="h4">
                PC : 101 - Introduction to Education
              </Typography> */}
              <CardMedia
                className={classes.media}
                // dynamic course image & title here
                image="https://images.unsplash.com/3/doctype-hi-res.jpg?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=746&q=80"
                title="Introduction to Education"
              />
            </Card>
          </Grid>

          {/* for details */}
          <Grid
            container
            className={classes.detailsContainer}
            justify="center"
            xs={12}
            lg={3}
          >
            Course details
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default CourseDetails;
