import { makeStyles } from "@material-ui/core/styles";

const coursesStyles = makeStyles((theme) => ({
  coursesContainer: {
    borderTop: "3 px solid #beae60",
    paddingTop: theme.spacing(3),
    paddingBottom: theme.spacing(3),
  },
}));

export default coursesStyles;
