import React, { useState } from "react";
import { Container, CssBaseline, Grid, Typography } from "@material-ui/core";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Box from "@material-ui/core/Box";

import courseDetailsStyles from "./CourseDetailsStyle";
import Header from "../../Header/Header";

const CourseDetails = () => {
  const classes = courseDetailsStyles();
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <>
      <CssBaseline />
      {/* header component start */}
      <Header></Header>
      {/* header component end */}
      <Container>
        {/* container to initially contain video player and details */}
        <Grid className={classes.courseDetailsContainer} container spacing={2}>
          {/* video player */}
          <Grid
            container
            className={classes.videoPlayerContainer}
            justify="flex-start"
            xs={12}
            lg={9}
          >
            {/* course title, will be dynamic */}
            <Typography variant="h4">
              PC : 101 - Introduction to Education
            </Typography>
            {/* video player here.. */}
            <img
              className={classes.courseImgContainer}
              src="https://images.unsplash.com/photo-1509062522246-3755977927d7?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=804&q=80"
              alt="course_img"
            />

            {/* tabs section */}
            <AppBar position="static">
              <Tabs
                value={value}
                onChange={handleChange}
                aria-label="simple tabs example"
              >
                <Tab label="Item One" {...a11yProps(0)} />
                <Tab label="Item Two" {...a11yProps(1)} />
                <Tab label="Item Three" {...a11yProps(2)} />
              </Tabs>
            </AppBar>
            {/* <TabPanel value={value} index={0}>
              Item One
            </TabPanel>
            <TabPanel value={value} index={1}>
              Item Two
            </TabPanel>
            <TabPanel value={value} index={2}>
              Item Three
            </TabPanel> */}
          </Grid>

          {/* for details */}
          <Grid
            container
            className={classes.detailsContainer}
            justify="center"
            xs={12}
            lg={3}
          >
            Course details
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default CourseDetails;
