import { makeStyles } from "@material-ui/core/styles";

const courseDetailsStyles = makeStyles((theme) => ({
  courseDetailsContainer: {
    marginTop: theme.spacing(3),
  },
  courseImgContainer: {
    marginTop: theme.spacing(3),
    height: "80%",
  },
}));

export default courseDetailsStyles;
