import React from "react";
import { Container, Grid, Typography } from "@material-ui/core";
import courseDetailsStyles from "./CourseDetailsStyle";
import Header from "../../Header/Header";

const CourseDetails = () => {
  const classes = courseDetailsStyles();
  return (
    <>
      {/* header component start */}
      <Header></Header>
      {/* header component end */}
      <Container>
        {/* container to initially contain video player and details */}
        <Grid className={classes.courseDetailsContainer} container spacing={2}>
          {/* video player */}
          <Grid
            container
            className={classes.videoPlayerContainer}
            justify="flex-start"
            xs={12}
            lg={9}
          >
            {/* course title, will be dynamic */}
            <Typography variant="h4">
              PC : 101 - Introduction to Education
            </Typography>
            {/* video player here.. */}
            <img
              className={classes.courseImg}
              xs={12}
              lg={12}
              src="https://images.unsplash.com/photo-1509062522246-3755977927d7?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=804&q=80"
              alt="course_img"
            />
          </Grid>

          {/* for details */}
          <Grid
            container
            className={classes.detailsContainer}
            justify="center"
            xs={12}
            lg={3}
          >
            Course details
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default CourseDetails;
