import { makeStyles } from "@material-ui/core/styles";

const coursesStyles = makeStyles((theme) => ({
  borderContainer: {
    borderColor: "#beae60",
  },
  categories: {
    paddingTop: theme.spacing(4),
  },
}));

export default coursesStyles;
